import { stringify } from "querystring";
import { useEffect, useRef, useState } from "react";
import TypeChecker from "../../../contents/js/validation";

const UserInsert = () => {
  const intialValues = {
    userId: "",
    userPw: "",
    userNm: "",
    pno: "",
    emailAddr: "",
    mblPno: "",
  };
  const [formValues, setFormValues] = useState(intialValues);
  const [formErrors, setFormErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const formRef = useRef<null[] | HTMLDivElement[]>([]);

  const submitForm = () => {
    console.log("submitForm start>>>>");
    console.log(formErrors);
    console.log(formValues);
    console.log("submitForm end>>>>");
  };
  const handleChange = (e: any) => {
    setFormValues({
      ...formValues,
      [e.target.name]: e.target.value,
    });
  };
  const handleSubmit = (e: any) => {
    e.preventDefault();
    setFormErrors(validate(formValues));
    setIsSubmitting(true);
  };
  const validate = (values: any) => {
    let errors: any = {};
    if (!values.userId) {
      errors.userId = "아이디를 입력하세요";
    }
    if (!values.userPw) {
      errors.userPw = "비밀번호를 입력하세요";
    }
    if (!TypeChecker.email(values.emailAddr)) {
      errors.emailAddr = "이메일은 " + TypeChecker.emailText;
    }
    return errors;
  };
  useEffect(() => {
    if (Object.keys(formErrors).length === 0 && isSubmitting) {
      submitForm();
    } else {
      let errorObj = formErrors;
      Object.keys(formErrors).find((key) => {
        console.log(formErrors);
        if (key === "userId") {
          alert(errorObj.userId);
        }
      });
    }
  }, [formErrors]); //submit을 눌러 formErrors가 변할때만 실행

  return (
    <form onSubmit={handleSubmit}>
      <div className="contents">
        <p className="contentTitle">사용자 목록</p>
        <p className="sub_path">
          <img
            src={require("@contents/images/ico_home.png")}
            width={10}
            height={9}
          />
          &nbsp;〉&nbsp;사용자관리&nbsp;〉&nbsp;사용자관리
        </p>
        <h4 className="contentTitle_h4">로그인 정보</h4>
        <div className="tableLayer">
          <table className="table">
            <caption />
            <colgroup>
              <col width="150px" />
              <col width="340px" />
              <col width="150px" />
              <col width="340px" />
            </colgroup>
            <tbody className="line">
              <tr>
                <th>아이디</th>
                <td>
                  <div className="commonSearch_wrap">
                    <label className="blind" htmlFor=" ">
                      d
                    </label>
                    <input
                      name="userId"
                      style={{ width: "320px" }}
                      type="text"
                      placeholder="test"
                      value={formValues.userId}
                      onChange={handleChange}
                      ref={(element) => {
                        formRef.current[0] = element;
                      }}
                    />
                  </div>
                </td>
                <th className="bullet_orange">패스워드</th>
                <td>
                  <div className="commonSearch_wrap">
                    <label className="blind" htmlFor=" ">
                      d
                    </label>
                    <input
                      name="userPw"
                      style={{ width: "320px" }}
                      type="password"
                      value={formValues.userPw}
                      onChange={handleChange}
                      ref={(element) => {
                        formRef.current[1] = element;
                      }}
                    />
                  </div>
                </td>
              </tr>
              <tr></tr>
            </tbody>
          </table>
        </div>
        <h4 className="contentTitle_h4">개인 정보</h4>
        <div className="tableLayer">
          <table className="table">
            <caption />
            <colgroup>
              <col width="150px" />
              <col width="340px" />
              <col width="150px" />
              <col width="340px" />
            </colgroup>

            <tbody className="line">
              <tr>
                <th className="bullet_orange">이름</th>
                <td>
                  <div className="commonSearch_wrap">
                    <label className="blind" htmlFor=" ">
                      d
                    </label>
                    <input
                      name="userNm"
                      style={{ width: "320px" }}
                      type="text"
                      placeholder="test"
                      value={formValues.userNm}
                      onChange={handleChange}
                      ref={(element) => {
                        formRef.current[2] = element;
                      }}
                    />
                  </div>
                </td>
                <th>전화번호</th>
                <td>
                  <div className="commonSearch_wrap">
                    <label className="blind" htmlFor=" ">
                      d
                    </label>
                    <input
                      name="pno"
                      style={{ width: "320px" }}
                      type="text"
                      placeholder="000-0000-0000"
                      value={formValues.pno}
                      onChange={handleChange}
                      ref={(element) => {
                        formRef.current[3] = element;
                      }}
                    />
                  </div>
                </td>
              </tr>
              <tr>
                <th>E-mail</th>
                <td>
                  <div className="commonSearch_wrap">
                    <label className="blind" htmlFor=" ">
                      d
                    </label>
                    <input
                      name="emailAddr"
                      style={{ width: "320px" }}
                      type="text"
                      value={formValues.emailAddr}
                      onChange={handleChange}
                      ref={(element) => {
                        formRef.current[4] = element;
                      }}
                    />
                  </div>
                </td>
                <th>휴대폰</th>
                <td>
                  <div className="commonSearch_wrap">
                    <label className="blind" htmlFor=" ">
                      d
                    </label>
                    <input
                      name="mblPno"
                      style={{ width: "320px" }}
                      type="text"
                      placeholder="000-0000-0000"
                      value={formValues.mblPno}
                      onChange={handleChange}
                    />
                  </div>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
        <div className="T_btnLayer fr">
          <a href="#">
            <button type="submit" className="blueBtn L">
              등록
            </button>
          </a>
          <a href="#">
            <button type="button" className="blueBtn L">
              취소
            </button>
          </a>
        </div>
      </div>
    </form>
  );
};

export default UserInsert;
